	class Output {
		public static function doGet ($e, $c, $l, $m, $a) {
			if (isset($e['uri'][0]) && $e['uri'][0] == 'source') {
				ob_start();
				highlight_file(__FILE__);
				return [
					'body' => [
						'MainBody' => ob_get_clean()
					]
				];
			}
		}
	}